import "./Navbar.css";
import { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Navbar({ setSearchQuery, onLogin, onLogout }) {
  const [searchInput, setSearchInput] = useState("");
  const [username, setUsername] = useState("");
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");
  const [bio, setBio] = useState("");
  const [password, setPassword] = useState("");
  const [identifier, setIdentifier] = useState("");
  const [currentJob, setCurrentJob] = useState("");
  const [number, setNumber] = useState("");

  const isUserSignedIn = !!localStorage.getItem("token");
  const [isSignupModalOpen, setIsSignupModalOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const navigate = useNavigate();

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchInput(query); // Update the local search input state
    setSearchQuery(query);  // Update the search query state in App
  };


  const openSignupModal = () => {
    setIsSignupModalOpen(true);
  };

  const closeSignupModal = () => {
    setIsSignupModalOpen(false);
  };

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setIsLoginModalOpen(false);
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/signup", {
        username,
        firstname,
        lastname,
        email,
        bio,
        currentJob,
        number,
        password,
      });
      closeSignupModal(); // Close the modal on successful signup
    } catch (err) {
      console.log(err);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3001/login", {
        identifier,
        password,
      });
      const token = response.data.token;
      onLogin(token);
      localStorage.setItem("token", token);
      closeLoginModal(); // Close the modal on successful login
      navigate("/"); // Navigate to the home page
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <nav>
        <div className="leftnav">
          <Link to="/">Almatacts</Link>
        </div>
        <div className="search">
          <input
            type="text"
            placeholder="Search"
            value={searchInput}
            onChange={handleSearch}
          />
        </div>
        <div className="rightnav">
          {isUserSignedIn ? (
            <button onClick={() => { onLogout(); navigate("/"); }}>Logout</button>
          ) : (
            <div className="entry">
              <button className="signupbutton" onClick={openSignupModal}>
                SignUp
              </button>
              <button className="loginbutton" onClick={openLoginModal}>
                LogIn
              </button>
            </div>
          )}
        </div>
      </nav>

      {/* Signup Modal */}
      {isSignupModalOpen && (
        <div className="modal" onClick={closeSignupModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={closeSignupModal}>&times;</span>
            <h3>Signup</h3>
            <form onSubmit={handleSignup}>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Firstname"
                value={firstname}
                onChange={(e) => setFirstname(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Lastname"
                value={lastname}
                onChange={(e) => setLastname(e.target.value)}
                required
              />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="current job"
                value={currentJob}
                onChange={(e) => setCurrentJob(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Bio"
                value={bio}
                onChange={(e) => setBio(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <input
                type="number"
                placeholder="Number"
                value={number}
                onChange={(e) => setNumber(e.target.value)}
                required
              />
              <button type="submit">Signup</button>
            </form>
          </div>
        </div>
      )}

      {/* Login Modal */}
      {isLoginModalOpen && (
        <div className="modal" onClick={closeLoginModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={closeLoginModal}>&times;</span>
            <h3>Login</h3>
            <form onSubmit={handleLogin}>
              <input
                type="text"
                placeholder="Username or Email"
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
}

export default Navbar;
