import Sidebar from "./Sidebar";
import Card from "./Card";
import { useEffect, useState } from "react";
import "./Home.css";
import axios from "axios";

function Home({ setSearchQuery }) {
  const [users, setUsers] = useState([]);
  const [localSearchQuery, setLocalSearchQuery] = useState(""); // Change variable name to avoid conflict

  useEffect(() => {
    axios
      .get("http://localhost:3001/allusers")
      .then((response) => {
        setUsers(response.data);
      })
      .catch((err) => console.error("Error fetching users:", err));
  }, []);

  // Update the search query state when the local search query changes
  useEffect(() => {
    setSearchQuery(localSearchQuery);
  }, [localSearchQuery, setSearchQuery]);

  const filteredUsers = users.filter((user) =>
    `${user.username} ${user.firstname} ${user.lastname}`
      .toLowerCase()
      .includes(localSearchQuery.toLowerCase())
  );

  return (
    <div className="both">
      <Sidebar />
      <div className="statncard">
        {filteredUsers.map((user) => (
          <Card
            key={user._id}
            id={user._id}
            username={user.username}
            firstname={user.firstname}
            lastname={user.lastname}
            email={user.email}
            currentJob={user.currentJob}
            bio={user.bio}
            number={user.number}
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
